mapper.select:
SELECT
COUNT(*)
FROM rec_test
WHERE date_format(rec_time,'%Y')=#{year}
GROUP BY DATE_FORMAT(rec_time,'%M')
ORDER BY DATE_FORMAT(rec_time,'%M');

SELECT
DATE_FORMAT(rec_time, '%d') as day
FROM rec_test
WHERE DATE_FORMAT(rec_time,'%Y-%m')=#{yearMonth}
GROUP BY day
ORDER BY day;

SELECT
COUNT(*)
FROM rec_test
WHERE DATE_FORMAT(rec_time,'%Y-%m')=#{yearMonth}
GROUP BY DATE_FORMAT(rec_time,'%d')
ORDER BY DATE_FORMAT(rec_time,'%d');
